import React from 'react';

class Search extends React.Component {

    constructor(props) {
        super()
        this.state = {
        }
    }

    render() {

    const searchResults = this.props.searchResults.map(curr => <li key={curr.id} data-id={curr.id} onClick={this.props.handleSelect} className="currency-list-item"><a href="#" className="currency" data-id={curr.id}><span>{curr.name}</span> <span>{curr.currency_symbol}</span></a></li>)

        return (
            <div>
                <h1>Cryptocurrency Portfolio Calculator</h1>
                <form>
                    <div className="form-group">
                        <label>Search for a currency:</label><br/>
                        <input onChange={this.props.handleChange} autoComplete="off" type="text" name="name" placeholder="Bitcoin, Litecoin, Ethereum, etc..." value={this.props.name} className="field"></input>
                    </div>
                    <div className="currency-list">
                        {searchResults}
                    </div>
                </form>
            </div>
        )
    }
}

export default Search;
