import React, {Component} from 'react'
import Search from './Search'
import Calculate from './Calculate'
import axios from 'axios'

class PortfolioContainer extends Component {

    constructor(props) {
        super(props)

        this.state = {
            name: '',
            portfolio: [],
            search_results: [],
            active_currency: null,
            amount: ''
            }

            this.handleSelect = this.handleSelect.bind(this)
    }



    handleChange = (e) => {
        axios.post('http://localhost:3000/search', {
            search: e.target.value
        })
        .then(data => {
            this.setState({
                search_results: [...data.data.currencies]
            })
        })
        .catch((data) => {
           console.log(data)
        })
    }

    // handleSelect = (e) => {
    //     e.preventDefault()
    //     const id = e.target.getAttribute('data-id')
    //     const activeCurrency = this.state.search_results.filter( item => item.id == id)
    //     this.setState({
    //         active_currency: activeCurrency[0],
    //         search_results: []
    //     })
    // }

    handleSelect(e){
        e.preventDefault()
        const id = e.target.getAttribute('data-id')
        console.log(id)
        const activeCurrency = this.state.search_results.filter( item => item.id == parseInt(id))

        this.setState({
          active_currency: activeCurrency[0],
          search_results: []
        })
      }

    render() {
        return(
            <div>
            <Search handleSelect={this.handleSelect} searchResults={this.state.search_results} handleChange={this.handleChange}/>
            <Calculate/>
            </div>
        )
    }
}

export default PortfolioContainer;
